import os
import math
import time
import usb.core
import usb.util
import usb.backend.libusb1 as libusb1
from importlib import resources as ir

# Now V0 is 0 on CON A
# V80 is V 39 on the board CON B
dac_map = list(range(80))  


# ============================== Protocol constants (A/B) ==============================

con_A = {
    'hand_signal_1': '',
    'hand_signal_2': '',
    'sync': '',
    'x_spr': '',
    'ldac_1': '',
    'ldac_2': ''
}
con_B = {
    'hand_signal_1': '',
    'hand_signal_2': '',
    'sync': '',
    'x_spr': '',
    'ldac_1': '',
    'ldac_2': ''
}


def _get_backend():
    try:
        import libusb_package
        for rel in [
            "bin/msvc/x64/libusb-1.0.dll",
            "bin/msvc/x86/libusb-1.0.dll",
            "bin/libusb-1.0.dll",
            "libusb-1.0.dll",
        ]:
            p = ir.files(libusb_package) / rel
            if p.is_file():
                if hasattr(os, "add_dll_directory"):
                    os.add_dll_directory(str(p.parent))
                be = libusb1.get_backend(find_library=lambda _: str(p))
                if be:
                    return be
    except:
        pass
    return libusb1.get_backend()


# ---------------- Controller ----------------
class DualAD5380Controller:
    def __init__(self):
        self.backend = _get_backend()
        self.dev, self.ep = self._connect()
        self.current_chip = None
        self.done = {'A': False, 'B': False}

    def _connect(self):
        dev = usb.core.find(idVendor=0x0456, backend=self.backend)
        if dev is None:
            raise RuntimeError("Board not found. Make sure power is ON and WinUSB/libusbK is bound.")
        dev.set_configuration()
        cfg = dev.get_active_configuration()
        intf = cfg[(0,0)]
        ep = usb.util.find_descriptor(intf,
              custom_match=lambda e: usb.util.endpoint_direction(e.bEndpointAddress)==usb.util.ENDPOINT_OUT)
        if ep is None:
            raise RuntimeError("No BULK OUT endpoint found.")
        usb.util.claim_interface(dev, intf.bInterfaceNumber)
        return dev, ep

    def _write(self, hex_data):
        self.ep.write(bytes.fromhex(hex_data), timeout=8000)

    def _handshake(self, chip):
        if self.done[chip]: return
        d = con_A if chip=='A' else con_B
        self._write(d['hand_signal_1'])
        time.sleep(0.04)
        self._write(d['hand_signal_2'])
        self.current_chip = chip
        self.done[chip] = True

    def _vol_hex(self, v):
        v = max(0, min(5, v))
        code = math.ceil(v * 0x3FFF / 5)
        h = f"{code:04x}"
        return h[2:] + f"{int(h[:2],16)+0xc0:02x}"

    def _phys(self, n):
        n = int(n)
        return dac_map[n]

    def _chip_and_index(self, p):
        return ('A', p) if p<40 else ('B', p-40)

    def set(self, ch, v):
        if isinstance(ch, int):
            chs, vs = [ch], [v]
        else:
            chs, vs = ch, v
        for c, val in zip(chs, vs):
            p = self._phys(c)
            chip, idx = self._chip_and_index(p)
            #It seems like we only needed when first connecting
            self._handshake(chip)
            d = con_A if chip=='A' else con_B
            self._write(d['ldac_1'])
            self._write(d['sync'])
            self._write(self._vol_hex(val) + f"{idx:02x}" + d['x_spr'])
            self._write(d['ldac_2'])

